import React, { forwardRef, useImperativeHandle, useState } from "react";
import { Card, Image, Alert, Modal } from "antd";
import { useNFTBalance } from "hooks/useNFTBalance";
const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "100%",
    gap: "10px"
  }
};

const ModalL3PBOnly = forwardRef(
  ({ handleNFTCancel, isModalNFTVisible, handleNFTOk, confirmLoading, getAsset, isMultiple = false }, ref) => {
    const { NFTBalance, fetchSuccess } = useNFTBalance();
    const [selectedNFTs, setSelectedNFTs] = useState([]);

    const handleClickCard = (nftItem) => {
      if (isMultiple) {
        if (
          selectedNFTs.some(
            (currentNft) =>
              `${currentNft.token_id}-${currentNft.token_address}` === `${nftItem.token_id}-${nftItem.token_address}`
          )
        ) {
          setSelectedNFTs(
            selectedNFTs.filter(
              (nft) => `${nft.token_id}-${nft.token_address}` !== `${nftItem.token_id}-${nftItem.token_address}`
            )
          );
        } else {
          setSelectedNFTs(selectedNFTs.concat([nftItem]));
        }
      } else {
        if (
          selectedNFTs.some(
            (currentNft) =>
              `${currentNft.token_id}-${currentNft.token_address}` === `${nftItem.token_id}-${nftItem.token_address}`
          )
        ) {
          setSelectedNFTs([]);
        } else {
          setSelectedNFTs([nftItem]);
        }
      }
    };

    const handleClickOk = () => {
      handleNFTOk(selectedNFTs);
    };

    useImperativeHandle(ref, () => ({
      reset() {
        setSelectedNFTs([]);
        handleNFTOk([]);
      }
    }));

    const L3PBundleBalance = NFTBalance.filter(function (results) {
      return results.token_address.includes("0xbc779ce41259cd7107ed2c36e00258b6234111bd"); // Edit to official Bundle contract address
    });

    return (
      <>
        <Modal
          width={"810px"}
          title='Select an L3PB bundle to unpack:'
          visible={isModalNFTVisible}
          onOk={handleClickOk}
          confirmLoading={confirmLoading}
          onCancel={handleNFTCancel}
        >
          <div style={styles.NFTs}>
            {!fetchSuccess && (
              <>
                <Alert
                  message='Unable to fetch all NFT metadata... We are searching for a solution, please try again later!'
                  type='warning'
                />
                <div style={{ marginBottom: "10px" }}></div>
              </>
            )}

            {L3PBundleBalance &&
              L3PBundleBalance.map((nft, index) => {
                return (
                  <Card
                    hoverable
                    style={{
                      width: 240,
                      border: selectedNFTs.some(
                        (nftItem) =>
                          `${nftItem.token_id}-${nftItem.token_address}` === `${nft.token_id}-${nft.token_address}`
                      )
                        ? "2px solid black"
                        : undefined,
                      opacity: selectedNFTs.some(
                        (nftItem) =>
                          `${nftItem.token_id}-${nftItem.token_address}` === `${nft.token_id}-${nft.token_address}`
                      )
                        ? "1"
                        : "0.8"
                    }}
                    cover={
                      <Image
                        preview={false}
                        src={nft?.image || "error"}
                        fallback='data:image/png;base64,'
                        alt=''
                        style={{ height: "240px" }}
                      />
                    }
                    key={index}
                    onClick={() => handleClickCard(nft)}
                  >
                    <Meta title={nft.name} description={nft.contract_type} />
                  </Card>
                );
              })}
          </div>
        </Modal>
      </>
    );
  }
);

export default ModalL3PBOnly;
